import org.dddappp.suiinscription.sui.contract.service.*;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

@Service
public class UpdateInscriptionStateTaskService {

    @Autowired
    private SuiInscriptionService suiInscriptionService;

    @Autowired
    private InscriptionEventRepository inscriptionEventRepository;

    @Autowired
    private InscriptionEventService inscriptionEventService;

    @Scheduled(fixedDelayString = "${sui.contract.update-inscription-states.fixed-delay:5000}")
    @Transactional
    public void updateInscriptionStates() {
        inscriptionEventRepository.findByStatusIsNull().forEach(e -> {
            String objectId = e.getId();
            if (InscriptionEventService.isDeletionCommand(e)) {
                suiInscriptionService.deleteInscription(objectId);
            } else {
                suiInscriptionService.updateInscriptionState(objectId);
            }
            inscriptionEventService.updateStatusToProcessed(e);
        });
    }
}
